export const loadMorePhones = async ({offset}) => {
  return new Promise(resolve => {
    resolve(phones)
  })
}

export const fetchPhoneById = async (id) => {
  const phone = R.find(R.propEq('id', id), phones)
  return new Promise(resolve => {
    resolve(phone)
  })
}

export const fetchCategories = async () => {
  return new Promise(resolve => {
    resolve(categories)
    //  reject('error')
  })
}